package com.Nestedloops;

public class Pattern_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1234
		//123
		//12
		//1
		
		for(int i=1;i<=4;i++)
		{
			for(int j=1; j>=i;j--)
			{
				System.out.print(i);
			}
			System.out.println();
		}
		
		
		
		
		
		
		
		
		
	}

}
