package com.loops;

public class Sum_Of_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  // write code display sum of even numbers between 1 to 20
		int sum=0;
		int i;
		for( i=1; i<=20; i++)
		{
			if(i%2==0) 
			{
				System.out.println(sum=sum+i);
			}
		}		
		
		
		
		
		
		
		
		
		
		
	}

}
