package com.loops;

public class While_Series_Square {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// write a code to display series to 10th term
		//1,4,9,16,25........
		
		int i=1;
		int square;
		while(i<=10)
		{
			square=i*i;
			i++;
			System.out.println(square);
			
			
			
		}
		
		
		
		
		
		
		
		
		
		
	}

}
